mapper.select:
select count(*) from TB_INV_INV 



    		mat_no = #{mat_no,jdbcType=VARCHAR}


    		And wh_id = #{wh_id,jdbcType=VARCHAR}

		select *
		from TB_INV_TXN




        		wh_id = #{wh_id,jdbcType=VARCHAR}


        		And mat_no = #{mat_no,jdbcType=VARCHAR}

mapper.update:
update TB_INV_INV
set booking_qty=booking_qty-#{qty}
where wh_id=#{whId}
and mat_no=#{matNo}

update TB_INV_INV
set
std_qty=std_qty-#{qty},
md_time=#{mdTime},
md_user=#{mdUser}
where wh_id=#{whId}
and mat_no=#{matNo}


    update TB_INV_INV
    set md_time=#{mdTime},
	  	md_user=#{mdUser},

			std_qty=std_qty+#{stdQty}


			wrd_qty=wrd_qty+#{wrdQty}


			wsp_qty=wsp_qty+#{wspQty}

		where mat_no=#{matNo}
		and wh_id=#{whId}


    update TB_INV_INV


        booking_qty = booking_qty-#{appQty},


    where mat_no=#{matNo}
		and wh_id=#{whId}


    update TB_INV_INV


        booking_qty = booking_qty + #{bookingQty},


        MD_USER =#{mdUser},


        MD_TIME = #{mdTime},


    where mat_no=#{matNO}
		and wh_id=#{whId}


    update TB_INV_INV


        booking_qty = booking_qty - #{bookingQty},


        MD_USER =#{mdUser},


        MD_TIME = #{mdTime},


    where mat_no=#{matNO}
		and wh_id=#{whId}

mapper.select:
select sum(std_qty)  std_qty
from tb_inv_inv 
where mat_no=#{matNo} 
and wh_id = #{whId} 
group by mat_no,wh_id
