mapper.select:
	WITH domaintree (id,name,parent) AS (
            SELECT id,name,parent
           FROM TB_ORG_DOMAIN
          WHERE id=#{domainId}
            UNION ALL
            SELECT t.id,t.name,t.parent
             FROM TB_ORG_DOMAIN t

             INNER JOIN domaintree ON domaintree.id =t.parent

        )
        SELECT DISTINCT m.* FROM domaintree m;
