mapper.select:

select 

from TB_PAY_OUTSOURCE_ACCEPTANCE_DTL A
left join TB_PAY_OUTSOURCE_ACCEPTANCE B on B.AP_NO = A.MST_AP_NO and B.PO_NO = A.PO_NO
left join TB_PAY_VOUCHER_CREDIT C on C.CASH_REQ_AP_NO = B.AP_NO and C.CONTRACT_PO_NO = B.PO_NO
left join TB_PAY_PAYMENT_REQUEST_VOUCHER D on D.SEQ_NBR = C.MST_SEQ_NBR
left join TB_COM_ITEM_MAIN E on E.ITEM_ID = A.ORDER_ITEM
where A.MST_AP_NO = #{MST_AP_NO,jdbcType=VARCHAR}
  and A.PO_NO = #{PO_NO,jdbcType=VARCHAR}
  and A.ORDER_NO = #{ORDER_NO,jdbcType=VARCHAR}
  and A.ORDER_ITEM = #{ORDER_ITEM,jdbcType=VARCHAR}
  and A.OS_ID = #{OS_ID,jdbcType=VARCHAR}

mapper.delete:

delete from TB_PAY_OUTSOURCE_ACCEPTANCE_DTL
where MST_AP_NO = #{MST_AP_NO,jdbcType=VARCHAR}
  and PO_NO = #{PO_NO,jdbcType=VARCHAR}
  and ORDER_NO = #{ORDER_NO,jdbcType=VARCHAR}
  and ORDER_ITEM = #{ORDER_ITEM,jdbcType=VARCHAR}
  and OS_ID = #{OS_ID,jdbcType=VARCHAR}

mapper.update:

update TB_PAY_OUTSOURCE_ACCEPTANCE_DTL


    UNIT_PRICE = #{UNIT_PRICE,jdbcType=DECIMAL},


    APP_QTY = #{APP_QTY,jdbcType=INTEGER},


    AP_QTY = #{AP_QTY,jdbcType=INTEGER},


    REAL_AP_QTY = #{REAL_AP_QTY,jdbcType=INTEGER},


    TAX_EXCLUSIVE_AMT = #{TAX_EXCLUSIVE_AMT,jdbcType=DECIMAL},


    BUSINESS_TAX = #{BUSINESS_TAX,jdbcType=DECIMAL},


    CR_USER = #{CR_USER,jdbcType=VARCHAR},


    CR_TIME = #{CR_TIME,jdbcType=TIMESTAMP},


    MD_USER = #{MD_USER,jdbcType=VARCHAR},


    MD_TIME = #{MD_TIME,jdbcType=TIMESTAMP},


where MST_AP_NO = #{MST_AP_NO,jdbcType=VARCHAR}
  and PO_NO = #{PO_NO,jdbcType=VARCHAR}
  and ORDER_NO = #{ORDER_NO,jdbcType=VARCHAR}
  and ORDER_ITEM = #{ORDER_ITEM,jdbcType=VARCHAR}
  and OS_ID = #{OS_ID,jdbcType=VARCHAR}


update TB_PAY_OUTSOURCE_ACCEPTANCE_DTL
set UNIT_PRICE = #{UNIT_PRICE,jdbcType=DECIMAL},
  APP_QTY = #{APP_QTY,jdbcType=INTEGER},
  AP_QTY = #{AP_QTY,jdbcType=INTEGER},
  REAL_AP_QTY = #{REAL_AP_QTY,jdbcType=INTEGER},
  TAX_EXCLUSIVE_AMT = #{TAX_EXCLUSIVE_AMT,jdbcType=DECIMAL},
  BUSINESS_TAX = #{BUSINESS_TAX,jdbcType=DECIMAL},
  CR_USER = #{CR_USER,jdbcType=VARCHAR},
  CR_TIME = #{CR_TIME,jdbcType=TIMESTAMP},
  MD_USER = #{MD_USER,jdbcType=VARCHAR},
  MD_TIME = #{MD_TIME,jdbcType=TIMESTAMP}
where MST_AP_NO = #{MST_AP_NO,jdbcType=VARCHAR}
  and PO_NO = #{PO_NO,jdbcType=VARCHAR}
  and ORDER_NO = #{ORDER_NO,jdbcType=VARCHAR}
  and ORDER_ITEM = #{ORDER_ITEM,jdbcType=VARCHAR}
  and OS_ID = #{OS_ID,jdbcType=VARCHAR}
