mapper.select:

select 

from TB_LS_LOC_ELEC_ADDED
where LS_NO = #{LS_NO,jdbcType=VARCHAR}
  and LS_VER = #{LS_VER,jdbcType=VARCHAR}
  and LOCATION_ID = #{LOCATION_ID,jdbcType=VARCHAR}
  and ENERGY_METER = #{ENERGY_METER,jdbcType=VARCHAR}
  and APP_SEQ = #{APP_SEQ,jdbcType=VARCHAR}
  and ADDED_STATE = #{ADDED_STATE,jdbcType=VARCHAR}

mapper.delete:

delete from TB_LS_LOC_ELEC_ADDED
where LS_NO = #{LS_NO,jdbcType=VARCHAR}
  and LS_VER = #{LS_VER,jdbcType=VARCHAR}
  and LOCATION_ID = #{LOCATION_ID,jdbcType=VARCHAR}
  and ENERGY_METER = #{ENERGY_METER,jdbcType=VARCHAR}
  and APP_SEQ = #{APP_SEQ,jdbcType=VARCHAR}
  and ADDED_STATE = #{ADDED_STATE,jdbcType=VARCHAR}
