mapper.select:
SELECT LS_NO FROM TB_LS_LOC_ELEC 
WHERE convert(date,GETDATE()) BETWEEN ELEC_BEGIN_DATE AND ELEC_END_DATE
    AND ENERGY_METER =  #{electricityMeterNbr}

SELECT A.ELEC_ADDR, A.ELEC_CUST_NAME, A.LOCATION_ID
  FROM TB_LS_LOC_ELEC A
 WHERE A.LS_NO = #{contractNo}
   AND A.LOCATION_ID IN
       (SELECT B.LOCATION_ID
          FROM TB_LS_SITE B
         WHERE B.LS_NO = #{contractNo}
           AND B.SITE_ID = #{siteId}
           AND CONVERT(DATE, #{appDate}) BETWEEN B.PAY_BEGIN_DATE AND B.LS_E_DATE
           AND B.EXPN_TYPE = 'E')
   AND A.ENERGY_METER = #{electricityMeterNbr}
   AND CONVERT(DATE, #{appDate}) BETWEEN A.ELEC_BEGIN_DATE AND A.ELEC_END_DATE

SELECT LOCATION_ID FROM TB_LS_LOC_ELEC
WHERE LS_NO = #{contractNo}
AND ENERGY_METER = #{electricityMeterNbr}
AND GETDATE() BETWEEN ELEC_BEGIN_DATE AND ELEC_END_DATE

Select b.NAME as siteName, a.LS_NO as LS_NO, a.LS_VER as LS_VER, a.LOCATION_ID as LOCATION_ID, a.ENERGY_METER as ENERGY_METER, a.ELEC_BEGIN_DATE as ELEC_BEGIN_DATE, a.ELEC_END_DATE as ELEC_END_DATE, a.ELEC_MODE as ELEC_MODE, a.CHRG_MODE as CHRG_MODE, a.BEGIN_DEGREE as BEGIN_DEGREE, a.END_DEGREE as END_DEGREE, a.ELEC_PLEDGE as ELEC_PLEDGE, a.PLDG_PAY_DATE as PLDG_PAY_DATE, a.ELEC_CUST_NAME as ELEC_CUST_NAME, a.ELEC_ADDR as ELEC_ADDR, a.ELEC_ADDR_STD as ELEC_ADDR_STD, a.PENALTY_DAY as PENALTY_DAY, a.PENALTY_DESC as PENALTY_DESC, a.SIGN_AGREEMENT as SIGN_AGREEMENT, a.FORM_ID as FORM_ID, a.CR_USER as CR_USER, a.CR_TIME as CR_TIME, a.MD_USER as MD_USER, a.MD_TIME as MD_TIME, a.org_ENERGY_METER,a.pldg_IN from TB_LS_LOC_ELEC as a left join TB_SYS_LOOKUP as b on a.ELEC_MODE = b.CODE where a.ls_no = #{lsNo}
and a.ls_ver =#{lsVer}
and a.LS_VER=(Select max(CONVERT(INT, LS_VER)) from TB_LS_LOC_ELEC where ls_no = #{lsNo} and LOCATION_ID = #{locId} )
and b.TYPE = 'LS_ELEC_MODE'
and a.LOCATION_ID = #{locId}

select A.energy_meter ELECTRICITY_METER_NBR, (select lookup_code_desc from TB_PAY_LOOKUP_CODE where lookup_type='process_type' and lookup_code=#{processType}) electricityType, A.chrg_mode, A.elec_begin_date, A.elec_end_date from TB_LS_LOC_ELEC A where A.ls_no=#{contractNo} and getDate() between elec_begin_date and elec_end_date
and A.elec_mode='E'
and A.pldg_pay_date is null and A.elec_pledge is not null ;

SELECT LS_NO, LS_VER, LOCATION_ID, CASE WHEN SUM (ELEC_PLEDGE) IS NULL THEN '0' ELSE SUM (ELEC_PLEDGE) END AS ELEC_PLEDGE FROM TB_LS_LOC_ELEC WHEREã€€ls_No = #{lsNo} and ls_Ver= #{lsVer} and location_Id = #{locationId}
and ELEC_MODE = #{elecMode}
GROUP BY LS_NO, LS_VER, LOCATION_ID
