select sitework.SITE_ID, workOrder.ORDER_ID, siteWork.WORK_ID, siteWork.WORK_TYPE, siteWork.BTS_SITE_ID, siteWork.SITE_NAME, siteWork.FEQ_ID, feq.FEQ_NAME, workOrder.REP_DEPT, dept.DEPT_NAME, workOrder.REP_TEAM, team.DEPT_NAME TEAM_NAME, workOrder.REP_USER, prs.CHN_NM REP_USER_NAME, workOrder.ODR_STATUS, siteEqp.EQP_NAME FROM TB_SITE_WORK siteWork join TB_SITE_WORK_ORDER workOrder ON siteWork.WORK_ID = workOrder.WORK_ID left outer join TB_COM_SITE_FEQ feq ON siteWork.FEQ_ID = feq.FEQ_ID left outer join TB_ORG_DEPT dept on dept.DEPT_ID = workOrder.REP_DEPT left outer join TB_ORG_DEPT team on team.DEPT_ID = workOrder.REP_TEAM left outer join TB_AUTH_PERSONNEL prs on prs.PSN_NO = workOrder.REP_USER left outer join TB_COM_EQP_TYPE siteEqp ON siteEqp.EQP_TYPE_ID = siteWork.EQP_TYPE_ID
and (workOrder.REP_DEPT = #{depId} OR workOrder.REP_TEAM = #{depId})
and workOrder.ODR_STATUS = #{orderStatus}
and siteWork.BTS_SITE_ID = #{btsSiteId}
and siteWork.SITE_NAME like '%'+#{siteName}+'%'

    select sgl.* from ( 
